mapper.select:
		SELECT *
  		FROM (SELECT @rn := @rn + 1 rn
             , a.*
          FROM (SELECT *
                  FROM control_board
                 WHERE User_info_employee_num = #{param1}
                ) a
             , (SELECT @rn := 0) b
        ) c
		where rn between #{param2} and #{param3}
		order by rn desc;

		SELECT *
  		FROM (SELECT @rn := @rn + 1 rn
